Scripts.OnClick:
 if (LootFilterButtonDeleteItems:GetText() ==
								LootFilter.Locale.LocText["LTDeleteItems"]) then
								LootFilter.sellQueue = 1;
								LootFilter.deleteItems(GetTime() +
								LootFilter.LOOT_TIMEOUT, true); 
								else
								LootFilter.sellQueue = 1;
								LootFilter.deleteItems(GetTime() +
								LootFilter.LOOT_TIMEOUT, false); 
								end;
